// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Confirmation from './confirmation';
import './App.css';
import axios from 'axios';

const App = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // Use useNavigate for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Here, you would normally send your message to the backend.
    // For example:
    // await axios.post('/api/messages', { message });

    // After successful submission, navigate to the confirmation page
    try {
      // Send the message to the Flask backend
      await axios.post('http://127.0.0.1:5000/api/messages', { message });

      // After successful submission, navigate to the confirmation page
      navigate('/confirmation');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="App">  {/* Updated className for main container */}
      <div className="container">
        <h1>Submit Your Message</h1>
        <form onSubmit={handleSubmit}>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message here..."
            rows="5"
            required
          ></textarea>
          <button type="submit" className="primary">Submit</button>
        </form>
      </div>
    </div>
  );
};

// Wrap App with Router and define routes
const AppWrapper = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<App />} />
        <Route path="/confirmation" element={<Confirmation />} />
      </Routes>
    </Router>
  );
};

export default AppWrapper;
